package IntroJava;

public class looptest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{ 
			//declare int BEFORE For loop to keep it
			
			int i = 0;
			
			//for(int i = 0; i < 10; i++)
			{
			System.out.println("The value of i is:");
			System.out.println(i);
			
			
			System.out.println(++i);
			
			
			System.out.println(i++);
			
			System.out.println(i);
		}
		}
}}

